import java.util.Scanner;
public class Codeup_75 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner stin = new Scanner(System.in);
		int a = stin.nextInt();
		
		while(a>=0)
		{
			a--;
			if(a<0)
				break;
			System.out.println(a);
			
			
			
		}
		 
	}

}
